let townList = null;

function searchCity(){
  fetch("http://localhost:3002/citys")
  .then(res=>res.json())
  .then((respuesta)=>{
    const ress = respuesta.map(el=>(
      {
        name: el.name
      }
    ))
    townList = ress
    })
   }

export default function SearchBar({onSearch}) {
  const [city, setCity] = useState("");
  const [search, setSearch] = []

useEffect(()=>{
  if(!townList){
    searchCity()
  }
},[city])




  return (
    <form onSubmit={(e) => {
      e.preventDefault();
      onSearch(city);
    }}>
      <input
        type="text"
        placeholder="Ciudad..."
        value={city}
        onChange={e => setCity(e.target.value)}
      />
      <input type="submit" value="Agregar" />
    </form>
  );
}
