import axios from "axios";
import React, { useEffect, useState } from "react";
import './Suggestiones.css';





export default function SearchBar({onSearch}) {
  const [names, setNames] = useState([])
  const [city, setCity] = useState("");
  const [suggestiones, setSuggestiones] = useState([])

  useEffect(()=>{
    const loadNames = async () => {
      const response = await axios.get('http://localhost:8080/')
      //console.log(response.data.map(el=>el.name))
      setNames(response.data)
    }
    loadNames()
  },[])
  const onChangeHandler = (city) =>{
    let matches = []
    if(city.length>0){
      matches = names.filter(user =>{
        const regex = new RegExp(`${city}`,"gi")
        return user.name.match(regex)
      })
    }
    console.log(matches)
    setSuggestiones(matches.slice(0,15))
    setCity(city)
  }

  return (
    <form className="padre" onSubmit={(e) => {
      e.preventDefault();
      onSearch(city);
    }}>
      <input
        type="text"
        placeholder="Ciudad..."
        value={city}
        onChange={e =>onChangeHandler(e.target.value)}
        //onChange={e => setCity(e.target.value) }
      />
      <input className="input" type="submit" value="Agregar" />
      {suggestiones && suggestiones.map((suggestiones)=>
      <div key={suggestiones.id} className = "Suggestiones">{suggestiones.name}, {suggestiones.country}</div>
      )}

    </form>

  );
}
