import React, { useEffect, useState } from "react";

let townList = null;



export default function SearchBar({onSearch}) {

  function searchCity() {
    fetch(`http://localhost:3002/citys`)
      .then(r => r.json())
      .then((recurso) => {
        if(recurso.name !== undefined){
          const ress = {
            name: recurso.name

          }
        townList = ress
        }
      });
  }




  const [city, setCity] = useState("");
  const [search, setSearch] = []

useEffect(()=>{
  if(!townList){
    searchCity()
  }
},[city])




  return (
    <form onSubmit={(e) => {
      e.preventDefault();
      onSearch(city);
    }}>
      <input
        type="text"
        placeholder="Ciudad..."
        value={city}
        onChange={e => setCity(e.target.value)}
      />
      <input type="submit" value="Agregar" />
    </form>
  );
}
