import React, { useState, useEffect } from 'react';

import './App.css';
import Nav from '../components/Nav.jsx';
import Cards from '../components/Cards.jsx';
// importo los componentes About y City
import About from '../components/About';
import City from '../components/City';
// importo Route:
import { Route } from 'react-router-dom';

const apiKey = '6a9b9651318ede2644e620f696ebfaf1';



function App() {
  const [cities, setCities] = useState([]);
  function onClose(id) {
    setCities(oldCities => oldCities.filter(c => c.id !== id));
  }

  function onSearch(ciudad) {
    //Llamado a la API del clima
    fetch(`http://api.openweathermap.org/data/2.5/weather?q=${ciudad}&appid=${apiKey}`)
      .then(r => r.json())
      .then((recurso) => {
        if(recurso.main !== undefined){
          const ciudad = {
            min: Math.round(recurso.main.temp_min),
            max: Math.round(recurso.main.temp_max),
            img: recurso.weather[0].icon,
            id: recurso.id,
            wind: recurso.wind.speed,
            temp: recurso.main.temp,
            name: recurso.name,
            weather: recurso.weather[0].main,
            clouds: recurso.clouds.all,
            latitud: recurso.coord.lat,
            longitud: recurso.coord.lon
          };
          setCities(oldCities => [...oldCities, ciudad]);
        } else {
          alert("Ciudad no encontrada");
        }
      });
  }



  window.addEventListener('load', ()=>{
    if(navigator.geolocation) navigator.geolocation.getCurrentPosition(posicion =>{
      let lat = posicion.coords.latitude;
      let lon = posicion.coords.longitude

      fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&APPID=${apiKey}&units=metric`)
    .then(c => c.json())
    .then((recurso)=>{
      if(recurso.main !== undefined){
        const names = {
          name: recurso.name 
        }
        useEffect(() => {
          onSearch(names.name);
        }, []);
      }
    })
    })
  })






  function onFilter(ciudadId) {
    let ciudad = cities.filter(c => c.id === parseInt(ciudadId));
    if(ciudad.length > 0) {
        return ciudad[0];
    } else {
        return null;
    }
  }
  const parana = "parana"
  const buenosaires = "buenos aires"
  const cordoba = "cordoba"






  useEffect(() => {
    onSearch(parana);
    onSearch(buenosaires);
    onSearch(cordoba)
  }, []); 
  return (
    <div className="App">
      <Route path='/' render={() => <Nav onSearch={onSearch} />}/>
      <Route path='/about' component={About}/>
      <Route exact path='/' render={() =>  <Cards cities={cities} onClose={onClose} />}/>
      <Route exact path='/ciudad/:ciudadId' 
             render={({match}) => (<City city={onFilter(match.params.ciudadId)}/>)}/>
      <hr />
    </div>
  );
}

export default App;
